import setaPlay from './img/seta_play.png'
import setaVirar from './img/seta_virar.png'
import { useState } from 'react'

export default function Pergunta({flashcard, pergunta, resposta, status, setStatus, contador, setContador}) {

    const [classe, setClasse] = useState('pergunta-fechada')
    const [texto, setTexto] = useState(flashcard)
    const [imagem, setImagem] = useState(setaPlay)
    const [funcao, setFuncao] = useState(() => abrirPergunta(this))

    console.log(status)

    function abrirPergunta(coisa){
        console.log(coisa)
        setStatus(!status)
        setClasse('pergunta-aberta')
        setTexto(pergunta)
        setImagem(setaVirar)
        setFuncao(() => abrirResposta)
    }

    function abrirResposta() {
        setTexto(resposta)
        setImagem('')
    }

    return (
        <>
            <div className={classe}>
                <p>{texto}</p>
                <img onClick={() => funcao(this)} src={imagem} />
            </div>
        </>
    )
}
