import setaPlay from './img/seta_play.png'
import setaVirar from './img/seta_virar.png'
import iconeCerto from './img/icone_certo.png'
import iconeErro from './img/icone_erro.png'
import iconeQuase from './img/icone_quase.png'
import party from './img/party.png'
import sad from './img/sad.png'
import { useState } from 'react'

export default function Pergunta() {
    const [perguntaZap, setPerguntaZap] = useState('pergunta-fechada')
    const [texto, setTexto] = useState ('pergunta 1')
    const [imagem, setImagem] = useState (setaPlay)
    const [funcao, setFuncao] = useState ('abrirPergunta')

    function abrirPergunta(){
        setPerguntaZap('pergunta-aberta')
        setTexto('O que é JSX?')
        setImagem(setaVirar)

    }

    function abrirResposta(){
        setPerguntaZap('pergunta-aberta')
        setTexto('Uma extensão de linguagem do JavaScript')
        setImagem('')
    }

    function finalizar(){
        alert('acabou!')
    }

    return (
        <>
            <div className={perguntaZap}>
                <p>{texto}</p>
                <img onClick={funcao} src={imagem} />
            </div>
        </>
    )
}
